import React, { Component } from "react";
import { Link } from 'react-router-dom';
import { Redirect } from "react-router";

let emptyUser = {
  fname: "",
  lname: "",
  email: "",
  password: ""
};

class LogARun extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: emptyUser,
      redirect: false
    }
  }

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(user => this.setState({ user: user[0] }))
  }

  // handleSubmit = e => {
  //   console.log(this.state.user);
  //   fetch("/users", {
  //     method: "POST",
  //     headers: {"Content-Type": "application/json"},
  //     body: JSON.stringify(this.state.user)
  //   });
  //   this.setState({user: emptyUser});
  //   this.setState({redirect: true});
  // };

  render() {
    //const {redirect} = this.state;
    return (
      <div className="logARun">
        <form onSubmit={(event) => {
          this.props.logRun(this.state.goalStatus);
          event.preventDefault();
        }
        }
          onChange={(event) => {
            let value = Number(event.target.value);
            this.setState({ goalStatus: value })
          }}>

          <input
            type="text"
            name="goal"
          />
          <button type="submit">Log a Run</button>
        </form>
        <h3>You're logging {this.state.goalStatus} miles today</h3>
        <Link to="/meetpet">
          <button>Log A Run</button>
        </Link>
      </div >
    )
  }
}



export default (LogARun);
