import React, { Component } from "react";
import { Link } from 'react-router-dom';
import { Redirect } from "react-router";

let emptyUser = {
  fname: "",
  lname: "",
  email: "",
  password: ""
};

class SignUp extends Component {
  state = {
    goal: emptyUser,
    redirect: false
  };

  handleSubmit = e => {
    console.log(this.state.user);
    fetch("https://localhost:3001/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(this.state.user)
    });
    this.setState({ goal: emptyUser });
    this.setState({ redirect: true });
  };

  render() {
    const { from } = this.props.location.state || "/";
    const { redirect } = this.state;

    return (
      <div>
        <form className="login" onSubmit={this.handleSubmit}>
          First Name:
          <input
            type="text"
            className="form-control"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, fname: e.target.value }
              });
            }}
          />
          Last Name
          <input
            type="text"
            className="form-control"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, lname: e.target.value }
              });
            }}
          />
          Email
          <input
            type="text"
            className="form-control"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, email: e.target.value }
              });
            }}
          />
          Password
          <input
            type="text"
            className="form-control"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, password: e.target.value }
              });
            }}
          />
          <input type="submit" value="save" />
          {redirect && <Redirect to={from || "/profilepage"} />}
        </form>
      </div>
    );
  }
}

class LogARun extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      goalStatus: 0,
    }
  }
  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(data => data.map((p) => console.log(p)))
  }

  render() {
    return (
      <div className="logARun">
        <form onSubmit={(event) => {
          this.props.logRun(this.state.goalStatus);
          event.preventDefault();
        }
        }
          onChange={(event) => {
            let value = Number(event.target.value);
            this.setState({ goalStatus: value })
          }}>

          <input
            type="text"
            name="goal"
          />
          <button type="submit">Log a Run</button>
        </form>
        <h3>You're logging {this.state.goalStatus} miles today</h3>
        <Link to="/meetpet">
          <button>Log A Run</button>
        </Link>
      </div >
    )
  }
}



export default (LogARun);
