import React, { Component } from "react";
import { Link } from 'react-router-dom';
import { Redirect } from "react-router";

let emptyUser = {
  fname: "",
  lname: "",
  email: "",
  password: "",
  distGoal: 0,
};

class LogARun extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: emptyUser,
      goal: 0,
      redirect: false
    }
  }

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(user => this.setState({ user: user[1] }))
  }

  handleSubmit = e => {
    console.log(this.state.user);
    fetch("/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(this.state.user)
    });
    this.setState({ user: emptyUser });
    this.setState({ redirect: true });
  };

  render() {
    //const {redirect} = this.state;
    return (
      <div className="logARun">
        <button onClick={() => {
          console.log(this.state.user.distGoal)
        }}></button>
        <form onSubmit={(event) => {
          this.handleSubmit()
          // this.props.logRun(this.state.goalStatus);
          // event.preventDefault();
        }
        }
          onChange={(event) => {
            let value = Number(event.target.value);
            this.setState({ goal: value })
          }}>

          <input
            type="text"
            name="goal"
          />
          <button type="submit">Log a Run</button>
        </form>
        <h3>You're logging {this.state.goal} miles today</h3>
        <Link to="/meetpet">
          <button>Log A Run</button>
        </Link>
      </div >
    )
  }
}



export default (LogARun);
